public class MathCS {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 15;
		int num2 = 7;
		int num3 = num1 + num2;
		//these exist because they were initialized just like the doubles and floats
		
		//float fnum = 10.0f; this is a floating point number
		
		
		System.out.println("the total is " + num3);
		
		float num4 = 1.1f;
		float num5 = 2.2f;
		float num6 = num5 + num4;
		System.out.println("the total is " + num6);
		
		double num7 = 2.36;
		double num8 = 5.68;
		double num9 = num8 + num7;
		System.out.println("the total is " + num9);
	}

}
